mapper.select:
SELECT 

FROM crm_contract a

WHERE a.id = #{id}

SELECT 

FROM crm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.payment_method = #{paymentMethod}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM crm_contract a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE crm_contract SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
