mapper.select:
SELECT 

FROM crm_customer a

WHERE a.id = #{id}

SELECT 

FROM crm_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sys_user_id = #{sysUser.id}


		AND a.vip = #{vip}


		AND a.source = #{source}


		AND a.classify = #{classify}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM crm_customer a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE crm_customer SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
