SELECT 

FROM crm_customer_follower a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.crm_customer_id = #{crmCustomer.id}


		AND a.sys_user_id = #{sysUser.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM crm_customer_follower a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
