mapper.select:
SELECT 

FROM erp_project a

WHERE a.id = #{id}

SELECT 

FROM erp_project a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.vip = #{vip}


		AND a.source = #{source}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM erp_project a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE erp_project SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
